DBI::dbExecute(con,"CREATE TABLE SVAL_RCH010_NA4_M AS SELECT 'SVAL_RCH010_NA4_M ' AS ID,RB010 AS YEAR,RB020,RB030, RCH010 ,RCH010_F ,RB081 , -4 AS EXP_FLAG,'RB081>15 ' AS ROUTING_CONDITION FROM R WHERE RCH010_F<>-4 AND RCH010_F<>-3 AND RCH010_F<>-7 AND RCH010_F<>-8 AND (RB081>15 ) AND RB081 IS NOT NULL ORDER BY RB010,RB020,RB030 ")
